import React from 'react';

export default function Home(props) {

  const handleID = ()=>{
    const reg = new RegExp(/([\d|a-f]+-){4}[\d|a-f]+/)
    if(!reg.test(localStorage.getItem("Anonymous-Chat-ID")))
      props.socket.send(JSON.stringify({
        type:'Genrate-Id' 
      }))
  }

  const handleInitRoom =(e , value)=>{
    e.preventDefault()
    props.setDisplay(value)
  }

  return (
    <div className="Home">
          <div className="wrapper fadeInDown">
      <div id="formContent">

        <h2 className="active">   Welcome Friend ,to Anonymous Chat   </h2>

        <div className="fadeIn first">
          <i className="fa fa-user-secret"></i>
        </div>

          <h4 className="fadeIn second" >ID: {props.ID == null? "You Dont Have Rigth Now":props.ID}  </h4>
          <input type="submit" onClick={handleID} className="fadeIn third" value="Genrate ID"/>
          <input type="text"  className="fadeIn fourth" placeholder="Enter Room ID"/>
          <input type="submit"  className="fadeIn fourth" value="Join Room"/>

        <div id="formFooter">
          <a href="/Call"  onClick={e => handleInitRoom(e ,"Call")} className="underlineHover"><i className="fa fa-phone"></i></a>
          <a href="/Message" onClick={e => handleInitRoom(e ,"Message")} className="underlineHover"><i className="fa fa-comments-o"></i></a>
        </div>

      </div>
    </div>
   </div>
  );
}
