import React, { useState }  from 'react';
import {usePeer} from '../Provider/PeerProvider'
import PeerJs from './PeerJs'

export default function Call(props) {

    const myPeer = usePeer()
    const [isHidden , setIsHidden] = useState(true)

    const handleX = ()=>{
        props.socket.send(JSON.stringify({
            type:'get-out'
        }))
     }

    const handleConnect = ()=>{
        props.socket.send(JSON.stringify({
            type:'init-connect',
            data:[document.getElementById("peerID").textContent , props.ID]
        }))
        setIsHidden(false)
    }
    props.socket.addEventListener('open' , event =>{
        // props.socket.send(JSON.stringify({
        //     type:'Refresh-Client',
        //     data:[
        //       localStorage.getItem('Anonymous-Chat-ID') ,
        //       window.sessionStorage.getItem('Anonymous-Chat-display'),
        //       window.sessionStorage.getItem('Anonymous-Chat-roomID')
        //     ]
        // }))
        console.log(document.getElementById("peerID").textContent)
      })

    return( 
        <div className="Call">
            <div className="wrapper fadeInDown">
                <div id="formContent">
                    <h3 id="peerID" className="active fadeIn first" ></h3>
                    <button onClick={handleX} className="fadeIn first">X</button>
                    {isHidden ?<button onClick={handleConnect} className="fadeIn first">Please Press Connect first</button>:''}
                    <div className="fadeIn second" id="video-grid">
                        <video id="me"></video>
                        <video id="friend"></video>
                        <PeerJs myPeer={myPeer}  roomID={props.roomID} socket={props.socket} ID={props.ID} />
                   </div>
                </div>
            </div>
        </div>
    )
}
