import React , {useEffect , useState} from 'react'


export default function Message(props) {


    let msg = props.chatLog.split(',')
    let isMe 
    const none = {
        textAlign:'initial'
    }

    const handleX = ()=>{
        props.socket.send(JSON.stringify({
            type:'get-out'
        }))
    }

    const sendMessage = (e) =>{
        e.preventDefault()
        const text = document.getElementById('message').value
        if(text === "" || text.search(',')) return 
        props.socket.send(JSON.stringify({
            type:'message-send',
            data:{message:text ,roomID:props.roomID,ID:props.ID}
        }))
        document.getElementById('message').value = ''
    }

    props.socket.addEventListener('message' , event =>{
        const messageObj = JSON.parse(event.data)
        switch (messageObj.type) {
            case 'message-recieve':
                console.log(msg ,  props.chatLog)
                props.setChatLog(msg +','+ messageObj.data.sender+','+ messageObj.data.message )
                msg = msg +','+ messageObj.data.sender+','+ messageObj.data.message
                console.log(msg)
                break
        }
    })

    return(
        <div className="Message">
            <button onClick={handleX} className="chat-exit">X</button>
            <div className="wrapper fadeInDown">     
                <div id="formContent" style={none} >    
                    <div className="fadeIn first Chat"> 
                    {
                        msg.map((m , index)=>{
                            if(index%2 === 0){
                                if(m === props.ID)
                                    isMe = 1
                                else
                                    isMe = 0
                            }else{
                                if(!isMe){
                                    return(
                                    <div key={index} className="outside">
                                        <i className="fa fa-user"></i>
                                        <div className="inside">
                                            {m}
                                        </div>
                                    </div>)
                                }else{
                                    return(
                                    <div  key={index} className="outside">
                                        <i className="fa fa-user"></i>
                                        <div className="inside-me">
                                            {m}
                                        </div>
                                    </div> )
                                }
                            }
                        })
                    }
                    </div>
                    <div className="fadeIn second send-box">
                        <input id="message" type="text" className="send-text" placeholder="Enter Message"/>
                        <button onClick={sendMessage}>Send</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
