import React  from 'react';
import {usePeer} from '../Provider/PeerProvider'
import PeerJs from './PeerJs'

export default function Call(props) {

    const myPeer = usePeer()

    const handleX = ()=>{
        props.socket.send(JSON.stringify({
            type:'get-out'
        }))
     }

    const handleConnect = ()=>{
        props.socket.send(JSON.stringify({
            type:'init-connect',
            data:document.getElementById("peerID").textContent
        }))
    }

    return( 
        <div className="Call">
            <div className="wrapper fadeInDown">
                <div id="formContent">
                     <button onClick={handleX} className="fadeIn first">X</button>
                    <button onClick={handleConnect} className="fadeIn first">Please Press Connect first</button>
                    <div className="fadeIn second" id="video-grid">
                        <video id="me"></video>
                        <video id="friend"></video>
                        <PeerJs myPeer={myPeer}  roomID={props.roomID} socket={props.socket} ID={props.ID} />
                   </div>
                </div>
            </div>
        </div>
    )
}
