import React from 'react'


export default function Message(props) {


    let text = props.chatLog.split(',')
    let isMe = 0;
    const none = {
        textAlign:'initial'
    }

    const handleX = ()=>{
        props.setDisplay("Home")
    }

    const sendMessage = (e) =>{
        e.preventDefault()
        const text = document.getElementById('message').value
        props.socket.send(JSON.stringify({
            type:'message-send',
            data:{message:text ,roomID:props.roomID,ID:props.ID}
        }))
        document.getElementById('standard-basic').value = ''
    }

    props.socket.addEventListener('message' , event =>{
        const messageObj = JSON.parse(event.data)
        switch (messageObj.type) {
            case 'message-recive':
                props.setChatLog(props.chatLog +','+ messageObj.data.sender+','+ messageObj.data.message )
                text = props.chatLog.split(',')
                break
        }
    })

    return(
        <div className="Message">
            <button onClick={handleX} className="chat-exit">X</button>
            <div className="wrapper fadeInDown">     
                <div id="formContent" style={none} >    
                    <div className="fadeIn first Chat"> 
                        {  
                        text.map( (text , index) =>{
                            if(index === 0) return
                            if(index % 2 === 1 ){
                                if(text === localStorage.getItem('Secrat-Chat-id'))
                                    isMe=1
                                else
                                    isMe=0
                            }
                            if(index % 2 === 0){
                                if(!isMe){
                                    return(
                                        <div className="outside">
                                            <i className="fa fa-user"></i>
                                            <div class="inside">
                                                {text}
                                            </div>
                                        </div>
                                    )
                                }else{
                                    return(
                                        <div className="outside">
                                            <i className="fa fa-user"></i>
                                            <div class="inside-me">
                                                {text}
                                            </div>
                                        </div>
                                    )
                                }
                            }
                        })
                    }
                    </div>
                    <div className="fadeIn second send-box">
                        <input id="message" type="text" className="send-text" placeholder="Enter Message"/>
                        <button onClick={sendMessage}>Send</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
