import  { useState } from 'react';
import Peer from 'peerjs'

export default function PeerJs(props) {

// eslint-disable-next-line
 let streamClient
 const [peer , setPeer] = useState()

 const myPeer = new Peer(undefined ,{
    host:'/',
    port:'3002'
 })


let myVideo = document.getElementById('me')

if(navigator.mediaDevices){
	navigator.mediaDevices.getUserMedia({
		video:true,
		audio:true
	}).then(stream =>{
		console.log(0)
		addVideoStream(myVideo , stream)
		streamClient = stream


myPeer.on('call' , call =>{
	console.log(2)
	call.answer(streamClient)
	const video = document.createElement('friend')
	call.on('stream' , userVideoStream =>{
		addVideoStream(video , userVideoStream)
	})
})
	}).catch(err=>{
		console.log("Error:" , err)
	})
}

// myPeer.on('open' , id=>{
// 	console.log(1)
// 	props.socket.send(JSON.stringify({
// 		type:'join-room',
// 		data:[props.roomID , props.ID]
// 	  }))
// })



function connectToNewUser(userId ,stream) {
	console.log(3)
	const call = myPeer.call(userId ,stream)
	const video = document.getElementById('friend')
	call.on('stream' , userVideoStream =>{
		addVideoStream(video , userVideoStream)
	})
	call.on('close' ,()=>{
		video.remove()
    })
    setPeer(call)
}

 function addVideoStream(video , stream) {
	video = document.getElementById('me')
	video.muted = true

 	video.srcObject = stream
 	video.addEventListener('loadedmetadata',() =>{
 		video.play();
 	})
 }

 props.socket.addEventListener('message' , event =>{
	const messageObj = JSON.parse(event.data)
	switch (messageObj.type) {
		case 'user-connected':
			connectToNewUser(messageObj.data ,streamClient)
		break
		case 'user-disconnected':
			if(peer) peer.close()
			break
		default:
			break
	}
})

return ('')
}
