import React, { useState } from 'react';
import {PeerServer as Peer} from '/usr/local/lib/node_modules/peer/index'
export default function Call(props) {


 const videoGrid = document.getElementById('video-grid')
 const [streamClient , setStreamClient] = useState()
 const [peer , setPeer] = useState()

 const myPeer = new Peer(undefined ,{
    host:'/',
    port:'3001'
})


const myVideo = document.createElement('video')
myVideo.muted = true

navigator.mediaDevices.getUserMedia({
	video:true,
	audio:true
}).then(stream =>{
	addVideoStream(myVideo , stream)

    setStreamClient(stream)

	myPeer.on('call' , call =>{
		call.answer(stream)
		const video = document.createElement('video')
		call.on('stream' , userVideoStream =>{
			addVideoStream(video , userVideoStream)
		})
	})

})

 myPeer.on('open' , ID =>{	
    props.socket.send(JSON.stringify({
        type:'join-room',
        data:[props.roomID , props.ID]
    }))
 })


function connectToNewUser(userId ,stream) {
	const call = myPeer.call(userId ,stream)
	const video = document.createElement('video')
	call.on('stream' , userVideoStream =>{
		addVideoStream(video , userVideoStream)
	})
	call.on('close' ,()=>{
		video.remove()
    })
    setPeer(call)
}

 function addVideoStream(video , stream) {
 	video.srcObject = stream
 	video.addEventListener('loadedmetadata',() =>{
 		video.play();
 	})
 	videoGrid.append(video)
 }


 props.socket.addEventListener('message' , event =>{
    const messageObj = JSON.parse(event.data)
    switch (messageObj.type) {
        case 'user-connected':
            connectToNewUser(messageObj.data ,streamClient)
            break
        case 'user-disconnected':
            if(peer) peer.close()
            break
        default:
            break
    }
})
    return( 
        <div className="Call">
            <div className="wrapper fadeInDown">
                <div id="formContent">
                    <div id="video-grid">

                   </div>
                </div>
            </div>
        </div>
    )
}
